import sqlite3
import os
from datetime import datetime
from werkzeug.security import generate_password_hash

def get_db_path():
    """Get the database path using os.path for production compatibility"""
    return os.path.join(os.path.dirname(__file__), 'escape_room.db')

def init_db():
    conn = sqlite3.connect(get_db_path())
    c = conn.cursor()
    
    # Drop existing table and create new one with game_url column
    c.execute('DROP TABLE IF EXISTS games')
    
    # Create new games table with game_url column
    c.execute('''CREATE TABLE games
                 (id INTEGER PRIMARY KEY AUTOINCREMENT,
                  title TEXT NOT NULL,
                  description TEXT,
                  rating REAL,
                  review_count INTEGER,
                  price REAL,
                  players_min INTEGER,
                  players_max INTEGER,
                  time_min INTEGER,
                  time_max INTEGER,
                  difficulty TEXT,
                  image_url TEXT,
                  game_url TEXT)''')  # Added game_url column
    
    # Create users table
    c.execute('''CREATE TABLE IF NOT EXISTS users
                 (id INTEGER PRIMARY KEY AUTOINCREMENT,
                  username TEXT UNIQUE NOT NULL,
                  email TEXT UNIQUE NOT NULL,
                  password TEXT NOT NULL,
                  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP)''')
    
    # Create FAQs table
    c.execute('''CREATE TABLE IF NOT EXISTS faqs
                 (id INTEGER PRIMARY KEY AUTOINCREMENT,
                  question TEXT NOT NULL,
                  answer TEXT NOT NULL)''')
    
    # Insert sample games data with game_url
    games = [
        ('Tetris Game', 
         'Tetris is a puzzle video game where players manipulate falling geometric shapes called tetrominoes to create complete horizontal lines in a rectangular playfield.', 
         4.1, 4456, 0.0, 1, 1, 30, 50, 'EASY', 
         "data:image/jpeg;base64, ",
         '/game1'),
        
        ('Puzzle', 
         'a person or thing that is difficult to understand or explain; an enigma.--', 
         4.6, 7054, 9.0, 1, 1, 40, 60, 'MODERATE', 
         "data:image/jpeg;base64,",
         '/game2'),
        
        ('Snake Game', 
         'Snakes are legless, carnivorous reptiles with scales, a forked tongue, and no eyelids or external ear openings', 
         4.5, 4207, 9.0, 1, 1, 50, 80, 'HARD', 
         "data:image/jpeg;base64,",
         '/game3')
    ]
    
    c.executemany('''INSERT INTO games 
                    (title, description, rating, review_count, price, players_min, players_max, time_min, time_max, difficulty, image_url, game_url)
                    VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)''', games)
    
    # Create default admin user
    default_password = generate_password_hash('admin123')
    c.execute('''INSERT OR IGNORE INTO users (username, email, password) 
                 VALUES (?, ?, ?)''', ('admin', 'admin@escape.com', default_password))
    
    # Clear existing FAQs
    c.execute('DELETE FROM faqs')
    
    # Insert FAQs data
    faqs = [
        ('Do I need experience to play?', 'No - our EASY and MODERATE games are designed to be enjoyed by everyone. We do recommend escape room experience before playing our HARD game.'),
        ('Do I need to install anything?', 'No downloads or installs are required. After you sign up you\'ll immediately receive a link via email - just click and play in your browser.'),
        ('Can we play from different places?', 'Yes. Each player joins via a web link, so you can connect from anywhere in the world. If you\'re together, you can also share a single device.'),
        ('How long do we get to play?', 'Your game link is valid for 6 months after signing up, so there is no rush. You can leave your game and come back to it later - your progress is saved.'),
        ('What if we get stuck?', 'All our games include a hint system to help you out if you need it. You can also "cheat" on individual puzzles if you\'re really stuck.'),
        ('What\'s your refund policy?', 'Your fun is our guarantee. If you\'re not satisfied with your experience, just email us and we\'ll give you a full refund - no hassle, no risk.')
    ]
    
    c.executemany('INSERT INTO faqs (question, answer) VALUES (?, ?)', faqs)
    
    conn.commit()
    conn.close()

def get_games():
    conn = sqlite3.connect(get_db_path())
    c = conn.cursor()
    c.execute('SELECT * FROM games')
    games = c.fetchall()
    conn.close()
    return games

def get_faqs():
    conn = sqlite3.connect(get_db_path())
    c = conn.cursor()
    c.execute('SELECT * FROM faqs')
    faqs = c.fetchall()
    conn.close()
    return faqs